mapper.insert:
INSERT INTO quality_check_item (
    category, rule_id, rule_name, severity, description,
    prompt_template, enabled, sort_order, created_at, updated_at
) VALUES (
    #{category}, #{ruleId}, #{ruleName}, #{severity}, #{description},
    #{promptTemplate}, #{enabled}, #{sortOrder}, #{createdAt}, #{updatedAt}
)

mapper.update:
UPDATE quality_check_item
category = #{category}, rule_id = #{ruleId}, rule_name = #{ruleName}, severity = #{severity}, description = #{description}, prompt_template = #{promptTemplate}, enabled = #{enabled}, sort_order = #{sortOrder}, updated_at = #{updatedAt}
WHERE id = #{id}
